import React, {useState, useEffect} from 'react';
import {
  Router,
  Route,
  Link,
} from 'react-router-dom';
import VerifiedUserRoundedIcon from '@material-ui/icons/VerifiedUserRounded';
import SendRoundedIcon from '@material-ui/icons/SendRounded';
import ReportRoundedIcon from '@material-ui/icons/ReportRounded';
import {PathLine} from 'react-svg-pathline'
import UnderStr from './underStrc';
import Section from './manager-sec';
import io from 'socket.io-client';
import host from '../constants/host'
import Graph from './graph.js'

const UserComp = ({name, username, verified, email, activity, manager}) => {
  const[v, setV] = useState('')
  const[m, setM] = useState('')
  useEffect(() => {
    if (verified) {
      setV(<VerifiedUserRoundedIcon style={{color:m}} />)
    }else {
      setV(<ReportRoundedIcon style={{color:'#DD2C00'}}/>)
    }
  },[1])

  useEffect(() => {
    if (manager) {
      setM('#2E7D32')
    }else {
      setM('#0091EA');
    }
  }, [manager])

  return (
      <div className='userCont' dir="rtl">
        <h1>{name}{v}</h1>
        <p dir="ltr">@{username}</p>
        <Graph activity={activity} />
      </div>
  );
}
export default UserComp;
