import React, {useState, useEffect} from 'react';

const AddUserInfoInput = ({h3, span, placeholder, p, className, type}) => {
  const[name, setName] = useState('');
  const[hed, setHed] = useState('');


  return (
    <>
      <h3>{h3} <span>{span}</span></h3>
      <input
        className="className"
        onChange={(e) => {
          setName(e.target.value);
        }}
        onBlur={(e) => {
          setHed(<p className="addUserInfo-p">{e.target.value}</p>);
          if (e.target.value == '') {
            setHed('')
          }
        }}
        value={name}
        type={type}
        placeholder={placeholder}
        required
      />
      <p className="addUserInfo-name-p">{p}</p>
    </>
      )
}
export default AddUserInfoInput;
