import React, {useState, useEffect} from 'react';

let c = [];
let color = 'white';
let r = Math.round(Math.random()*99);
for (var i = 1; i <= 100; i++) {
  c.push(i)
}

const Random = () => {

  const cell = c.map((item, i) => (<p key={i} id={"cell"+i} style={{backgroundColor: color}} className="randomCell">{i}</p>))

  useEffect(()=>{
    setTimeout(function () {
      let rand = r;
      for (var i = 0; i < 30; i++) {
        console.warn(rand);
        document.getElementById('cell'+rand+'').style.backgroundColor = '#87255B';
        let arrow = ['up', 'right', 'down', 'left']
        let randArrow =  arrow[Math.round(Math.random()*3)];
        console.log(randArrow);
        if (randArrow == 'left') {
          if ((rand+1)%10 == 0) {
            if (document.getElementById('cell'+(rand-1)+'').style.backgroundColor != '#87255B') {
              rand--;
            }
          }else {
            if (document.getElementById('cell'+(rand+1)+'').style.backgroundColor != '#87255B') {
              rand++;
            }
          }
        }if (randArrow == 'right') {
          if (rand%10 == 0) {
            rand++;
          }else {
            rand--;
          }
        }
        if (randArrow == 'up') {
          rand = rand - 10;
        }
        if (randArrow == 'down') {
          rand = rand + 10;
        }
        console.error(rand);
      }
    }, 1000);
  },[cell])

  return (
    <p className="random">{cell}</p>
  );
}
export default Random;
