import React, {useState, useEffect} from 'react';
import {
  Router,
  Route,
  Link,
} from 'react-router-dom';
import UnderStr from './underStrc';
import Section from './manager-sec';
import io from 'socket.io-client';
import host from '../constants/host'
import UserComp from './userComp'
import UserProfile from './userProfile.js'
const socket = io.connect(host,{transports: ['websocket']});

const Users = ({changePath, addUserInfo, changeBackIconPath, logedInUser}) => {
  const[user, setUser] = useState('')
  const[profile, setProfile] = useState('')
  const[array, setArray] = useState([])
  const[p, setP] = useState()

  useEffect(()=>{
    changeBackIconPath('/manager')
    socket.emit('giveMeUsers', logedInUser)
  }, [0])

  useEffect(()=>{
    socket.on('users', function (e) {
      setUser(e.map((item,i) => (<UserComp key={i}
        name={item.name}
        activity={item.activity}
        username={item.username}
        verified={item.isUser}
        phone={item.phone.p}
        manager={item.manager}
        money={item.allMoney}
        debt={item.debt}
        clickOn={clickOn}
        />)))
    })
  },[array])
  useEffect(()=>{
    if (p) {
      console.log(p);
      setProfile(p.map((item,i) => (<UserProfile
        key={i}
        name={item[0]}
        username={item[1]}
        verified={item[2]}
        phone={item.[3]}
        activity={item[4]}
        manager={item[5]}
        money={item[6]}
        debt={item[7]}
        />)))
    }
  },[p])

  const clickOn = (e) => {
    setP(e)
  }



  return (
    <div className="addInfo-cont" >
      <Route path={'/manager/users'} exact>
        {user}
      </Route>
      <Route path={"/manager/users/:id"} >
        <UserProfile />
      </Route>
    </div>
  );
}
export default Users;
