import React, {useState, useEffect} from 'react';
import {
  Router,
  Route,
  Link,
} from 'react-router-dom';
import {PathLine} from 'react-svg-pathline'
import UnderStr from './underStrc';
import Section from './manager-sec';
import io from 'socket.io-client';
import host from '../constants/host'
const socket = io.connect(host,{transports: ['websocket']});

let day = [];
let d = new Date();
let thisDay = 1;

for (var i = 0; i < 30; i++) {
  thisDay --;
  if (thisDay<1) {
    thisDay = thisDay + 30;
  }
  day.push({x:thisDay, y:100})
}



const Graph = ({activity}) => {
  const [data, setData] = useState()
  const [g, setG] = useState(day)
  let c = 0;
  let h = {};
  let pLine = [];

  useEffect(() => {
    const sd = activity.map((item, i) =>{
      let d = item.split(' ')[0].split('/')[2];
      let f = activity[i+1];
      h[d] = c+1;
      if (f) {
        //console.log(item, activity[i+1]);
        if (d != f.split(' ')[0].split('/')[2]) {
          c = 0;
        }else {
          c++
        }
      }
      return h
    });
    setData(sd[0]);
  }, [1])



  useEffect(() => {
    day.forEach((item, i) => {
      if (data) {
        //console.log(data);
        let xX = 10*(item.x), yY = 10*(item.y);
        for (var j = 0; j < Object.keys(data).length; j++) {
          if (xX == 10*(Object.keys(data)[j])) {
            console.log(100-Object.values(data)[j]);
            pLine.push({x:xX, y:(1000-Object.values(data)[j])})
          }else {
            if (!pLine[i]) {
              pLine.push({x:xX, y:yY})
            }
          }
        }
      }
    });
    if (pLine != '') {
      setG(pLine);
      console.log(pLine);
    }
  }, [data])

  return (
    <>
      <svg className="activityDiag" width={210} height={100}>
        <PathLine
         className="activityLine"
         points={g}
         stroke="#00897B"
         strokeWidth="1"
         fill="none"
         strokeLinecap="round"
         r={1}
         />
      </svg>
      <h1></h1>
      </>
  );
}
export default Graph;
