import React, {useState, useEffect} from 'react';
import {
  Router,
  Route,
  Link,
} from 'react-router-dom';
import VerifiedUserRoundedIcon from '@material-ui/icons/VerifiedUserRounded';
import PaymentIcon from '@material-ui/icons/Payment';
import MoneyIcon from '@material-ui/icons/Money';
import ReportRoundedIcon from '@material-ui/icons/ReportRounded';
import {PathLine} from 'react-svg-pathline'
import UnderStr from './underStrc';
import Section from './manager-sec';
import io from 'socket.io-client';
import host from '../constants/host'
import Graph from './graph.js'

const UserComp = ({name, username, verified, email, activity, manager, money, debt}) => {
  const[v, setV] = useState('')
  const[m, setM] = useState('')
  const[i, setI] = useState('')


  useEffect(() => {
    if (verified) {
      if (manager) {
        setV(<VerifiedUserRoundedIcon className="userContIcon" style={{color:'#2E7D32', fontSize:30, paddingRight:3}} />)
        setI('مدیر')
      }else {
        setV(<VerifiedUserRoundedIcon className="userContIcon" style={{color:'#0091EA', fontSize:30}} />)
        setI('تایید شده')
      }
    }else {
      setV(<ReportRoundedIcon className="noVerified userContIcon" style={{color:'#DD2C00', fontSize:30}}/>)
      setI('تایید نشده')
    }
  },[1])

  return (
      <div className='userCont' dir="rtl">
        <div className="userContR">
          <h1>{name}</h1>
          <p dir="ltr">@{username}</p>
          <Graph activity={activity} />
        </div>
        <div className="userContL">
          <div className="userContInfo">
            <p className="userContChip">
              <p className="userContChipP">{debt} قسط باقی</p>
              <PaymentIcon style={{borderRight:'solid #999 1px', paddingRight: 5, fontSize:30}}/>
            </p>
          </div>
          <div className="userContIconCont">
             <p className={"userContIconi"} style={{padding:"0 0 0 10px"}}>{i}</p>
           {v}
          </div>
        </div>
      </div>
  );
}
export default UserComp;
