import React, {useState, useEffect} from 'react';
import {
  Route,
  Link,
} from 'react-router-dom';

let d = new Date()
console.log(d.getYear()+"-"+d.getMonth()+"-"+d.getDay());

const AddUserInfo = ({changePath}) => {
  const[name, setName] = useState('')
  const[nameDate, setNameDate] = useState('2020-12-16')
  const[nameGroup, setNameGroup] = useState()
  const[hed, setHed] = useState('')
  const[counter, setCounter] = useState(0)


  return (
    <div className="addUserInfo-cont" dir="rtl">
      <h1 className="addUserInfo-h1">ثبت اطلاعات کاربر</h1>
      {hed}
      <div className="addUserInfo-row1">
        <h3>نام کاربر</h3>
        <input
          className="addUserInfo-name-in login-input"
          onChange={(e) => {
            setName(e.target.value);
          }}
          onBlur={(e) => {
            setHed(<p className="addUserInfo-p">{e.target.value}</p>);
            if (e.target.value == '') {
              setHed('')
            }
          }}
          value={name}
          placeholder="لطفا نام کاربر را کامل وارد کنید"
          required
        />
        <details
        className="addUserInfo-name-more"
        onClick={() => {
          if (counter == 0) {
            document.querySelector('.addUserInfo-name-more > summary').style.padding = '7px 7px 15px 7px';
            document.querySelector('.addUserInfo-name-more > summary').style.borderRadius = '10px 10px 0 0';
            setCounter(1);
          }
          else {
            document.querySelector('.addUserInfo-name-more > summary').style.padding = '7px';
            document.querySelector('.addUserInfo-name-more > summary').style.borderRadius = '10px';
            setCounter(0);
          }
          }}
        >
          <summary>اطلاعات بیشتری برای وارد کردن هست؟</summary>
          <div
          style={{padding:'30px 10px',borderTop:'solid #eee 1px',backgroundColor: '#fbfbfb', borderRadius:'0 0 10px 10px'}}
          onClick={(e) => {
            e.stopPropagation();
          }}
          >
            <h3>تاریخ عضویت کاربر</h3>
            <input
              className="addUserInfo-date-in login-input"
              type="date"
              onChange={(e) => {
                setNameDate(e.target.value);
              }}
              value={nameDate}
              dateFormat= 'yy'
              placeholder="DD-MMMM-YYYY"
            />
            <h3>دسته بندی کاربر</h3>
            <input
              className="addUserInfo-date-in login-input"
              onChange={(e) => {
                setNameDate(e.target.value);
              }}
              value={nameGroup}
              placeholder="یک گروه برای دسته بندی کاربر وارد کنید"
            />
          </div>
        </details>
      </div>
    </div>
  );
}
export default AddUserInfo;
