import React, {useState} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Link,
  Route,
  Redirect,
} from 'react-router-dom';
import Header from './components/header';
import Login from './components/login';
import Register from './components/register';
import Footer from './components/main-footer';

const App = () => {
  const[registered, setRegistered] = useState(true);

  const isVerified = (e) => {
    if (e) {
      setRegistered(true)
    }
  }

  return (
    <Router>
      <Switch>
        <Route path={'/'} exact>
          <Header />
          <Login />
          <Footer />
        </Route>
        <Route path={'/login'}>
          <Header />
          <Login/>
          <Footer />
        </Route>
        <Route path={'/register'}>
          <Header />
          <Register isVerified={isVerified}/>
          <Footer />
          {(registered === true) ? <Redirect to={'/home'} /> : <Redirect to={'/register'} />}
        </Route>
        <Route path={'*'}>
            404 Error
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
