import React, {useState} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from 'react-router-dom';

const Section = ({path, title, content}) => {
  const[counter, setCounter] = useState(0)
  const[status, setStatus] = useState(false)
  const[decoration, setDecoration] = useState('none')
  const[opacity, setOpacity] = useState('none')
  const[ticked, setTicked] = useState('none')

  return (
    <div className="section" dir="rtl" done={status}>
      <h1 className="section-title" style={{textDecoration:decoration}}>
        <span>{title}</span>
        <div className="done" onClick={
          (e) => {
            e.stopPropagation();
            if (counter == 0) {
              setStatus(true);
              setDecoration('line-through')
              setCounter(1);
            }else {
              setStatus(false);
              setDecoration('none')
              setCounter(0);
            }
          }
        }></div>
      </h1>
      <p className="section-contant" style={{textDecoration:decoration}}>{content}</p>
    </div>
  );
}

export default Section;
