import React, {useState, useEffect} from 'react';
import {
  Router,
  Route,
  Link,
  useParams
} from 'react-router-dom';

const UserProfile = ({name, username, verified, email, activity, manager, money, debt}) => {
  const[v, setV] = useState('')
  const[m, setM] = useState('')
  const[i, setI] = useState('')
  const[u, setU] = useState("@"+username)
  const[d, setD] = useState(debt+" قسط")

  useEffect(()=>{
    if (verified) {
      if (manager) {
        setV('#7CBB00')
        setI('مدیر')
      }else {
        if (verified === true) {
          setV('#00A1F1')
          setI('تایید شده')
        }else if (verified.ncode && verified.name) {
          setV('#FFBB00')
          setI('در انتظار تایید')
        }else {
          setV('#FFBB00')
          setI('در انتظار تایید')
        }
      }
    }else {
      setV('#F65314')
      setI('تایید نشده')
    }
  })

  return (
    <div
    dir={"rtl"}
    className={"userProfileCont"}
    >
      <h1 className={"userProfileHed"} >{name}</h1>
      <p className={"userProfileStat"}>وضعیت: {i}</p>
    </div>
  );
}
export default UserProfile;
