import React, {useState, useEffect} from 'react';
import {
  Router,
  Route,
  Link,
  useParams
} from 'react-router-dom';

const UserProfile = ({joinDate, name, username, verified, email, activity, manager, money, debt, ncode, phone}) => {
  const[v, setV] = useState('')
  const[c, setC] = useState('')
  const[m, setM] = useState('')
  const[i, setI] = useState('')
  const[u, setU] = useState("@"+username)
  const[d, setD] = useState(debt+" قسط")
  const[phoneN, setPhoneN] = useState("_")
  const[message, setMessage] = useState("")
  const[how, setHow] = useState("")
  const[date, setDate] = useState("")

  useEffect(()=>{
    if (verified) {
      if (manager) {
        setV('#7CBB00')
        setC('#f9fbe7')
        setI('مدیر ')
        setM('مدیر')
      }else {
        if (verified === true) {
          setV('#00A1F1')
          setC('#e0f7fa')
          setI('تایید شده ')
          setPhoneN(phone);
          setMessage('هویت این کاربر توسط '+how+' در تاریخ '+date+' تایید شده است.')
        }else if (verified.ncode && verified.name) {
          setV('#FFBB00')
          setC('#fffde7')
          setI('در انتظار تایید ')
          setM('تایید شده')
          setPhoneN(phone);
          setMessage("ما تشخیص داده ایم که احتمالا این کاربر از قبل عضو صندوق بوده است. این کاربر در تاریخ "+joinDate+" درخواست عضویت داده است. لطفا اطلاعات او را دقیق بررسی کنید و درصورت امکان با او تماس بگیرید.")
        }else {
          setV('#FFBB00')
          setC('#fffde7')
          setI('در انتظار تایید ')
          setI('در انتظار تایید ')
        }
      }
    }else {
      setV('#F65314')
      setC('#fbe9e7')
      setI('تایید نشده ')
      setM('تایید نشده ')
      setMessage('ما نتوانستیم هویت این کاربر را تایید کنیم. ممکن است یک عضو جدید باشد. این کاربر در تاریخ '+`<span dir="ltr">${joinDate}</span>`+' درخواست عضویت داده است.اگر او را می شناسید با او تماس بگیرید تا اطمینان حاصل کنید. در غیر اینصورت میتوانید اورا حذف کنید.')
    }
  })

  console.log(phone);

  return (
    <div
    dir={"rtl"}
    className={"userProfileCont"}
    >
      <h1 className={"userProfileHed"} >{name}</h1>
      <p className={"userProfileStat"} style={{color:v, border: `solid ${v} 1px`, backgroundColor:c}}>
        <h3 style={{borderBottom:`solid ${v} 1px`}}>وضعیت: {i}
          <span style={{fontSize:12}}>(کلیک کنید)</span>
        </h3>
        <div className={"userProfileStatDit"}>
          <p><span>کد ملی ({m}):</span><span>{(ncode.toString().split('').length == 10)?(ncode):
          (ncode.toString().split('').length == 9)?("0"+ncode):("00"+ncode)}</span></p>
          <p className={"userProfileStatDitMess"}>{message}</p>
        </div>
      </p>
    </div>
  );
}
export default UserProfile;
