import React, {useState, useEffect} from 'react';


const Random = () => {

  let cell = [];
  let rand = Math.round(Math.random()*30);
  for (var i = 1; i <= 100; i++) {
    let color = 'white';
    if (i = rand) {
      color='#65743A'
    }
    cell.push(<div key={i} style={{backgroundColor: color}} className="randomCell"></div>)
  }

  return (
    <div className="random">{cell}</div>
  );
}
export default Random;
