import React, {useState, useEffect} from 'react';

let c = [];
let color = 'white';
let rand = Math.round(Math.random()*30);
for (var i = 1; i <= 100; i++) {
  c.push(i)
}

const Random = () => {

  const cell = c.map((item, i) => (<p key={i} id={i} style={{backgroundColor: color}} className="randomCell">{i}</p>))

  useEffect(()=>{
    let x = 0;
    do {
      x++
      console.log(x);
      document.getElementById(''+rand+'').style.backgroundColor = '#87255B';
      let arrow = ['up', 'right', 'down', 'left']
      let randArrow =  Math.round(Math.random()*3);
      if (randArrow == 'left') {
        if ((rand+1)%10 == 0) {
          rand--;
        }else {
          rand++;
        }
      }if (randArrow == 'right') {
        if (rand%10 == 0) {
          rand++;
        }else {
          rand--;
        }
      }
      if (randArrow == 'up') {
        rand = rand + 10;
      }
      if (randArrow == 'down') {
        rand = rand - 10;
      }
    } while (x > 30);
  })

  return (
    <p className="random">{cell}</p>
  );
}
export default Random;
