import React, {useState, useEffect} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from 'react-router-dom';
import UnderStr from './underStrc';
import Section from './manager-sec';
import sectionContent from '../constants/section-content.js'

const Main = () => {
  const[list, setList] = useState(sectionContent)
  const[sectionComp, setSectionComp] = useState(sectionContent)

  const onTicked = (e, k) =>{
    console.log(list);
    setList(list.splice(k, -1))
    console.log(list);
  }

  useEffect(()=>{
    console.log(list);
    setSectionComp(
      list.map((item, i) => (
        <Section
        id={i}
        key={item[0]}
        path={item[3]}
        title={item[1]}
        content={item[2]}
        onTicked={onTicked}
        />
    ))
  )
}, [list])

  return (
    <div className="section-cont">
      <Switch>
        <Route path={'/manager/add'}>
          <UnderStr />
        </Route>
        <Route path={'/manager'}>
          {sectionComp}
        </Route>
      </Switch>
    </div>
  );
}

export default Main;
