import React, {useState, useEffect} from 'react';
import {
  Router,
  Route,
  Link,
  useParams
} from 'react-router-dom';

const UserProfile = ({name, username, verified, email, activity, manager, money, debt}) => {
  const[v, setV] = useState('')
  const[c, setC] = useState('')
  const[m, setM] = useState('')
  const[i, setI] = useState('')
  const[u, setU] = useState("@"+username)
  const[d, setD] = useState(debt+" قسط")

  useEffect(()=>{
    if (verified) {
      if (manager) {
        setV('#7CBB00')
        setC('#f9fbe7')
        setI('مدیر ')
      }else {
        if (verified === true) {
          setV('#00A1F1')
          setC('#e0f7fa')
          setI('تایید شده ')
        }else if (verified.ncode && verified.name) {
          setV('#FFBB00')
          setC('#fffde7')
          setI('در انتظار تایید ')
          setM('تایید شده')
        }else {
          setV('#FFBB00')
          setC('#fffde7')
          setI('در انتظار تایید ')
        }
      }
    }else {
      setV('#F65314')
      setC('#fbe9e7')
      setI('تایید نشده ')
    }
  })

  return (
    <div
    dir={"rtl"}
    className={"userProfileCont"}
    >
      <h1 className={"userProfileHed"} >{name}</h1>
      <p className={"userProfileStat"} style={{color:v, border: `solid ${v} 1px`, backgroundColor:c}}>
        <h3 style={{borderBottom:`solid ${v} 1px`}}>وضعیت: {i}
          <span style={{fontSize:12}}>(کلیک کنید)</span>
        </h3>
        <div className={"userProfileStatDit"}>
          <p><span>کد ملی ({i}):</span></p>
        </div>
      </p>
    </div>
  );
}
export default UserProfile;
