import React, {useState, useEffect} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from 'react-router-dom';
import UnderStr from './underStrc';
import Section from './manager-sec';
import sectionContent from '../constants/section-content.js'

const Main = () => {
  const[list, setList] = useState(sectionContent)
  const[sectionComp, setSectionComp] = useState(sectionContent)
  const[il, setIl] = useState()

  const onTicked = (e, k) =>{
    setIl(k)
  }

  useEffect(()=>{
    setSectionComp(
      list.map((item, i) => (() => {
        if (i != il)
          { <Section id={i} key={item[0]} path={item[3]} title={item[1]} content={item[2]} onTicked={onTicked} /> }
        else {
          ''
        }
      }

    ))
  )
}, [il])

  return (
    <div className="section-cont">
      <Switch>
        <Route path={'/manager/add'}>
          <UnderStr />
        </Route>
        <Route path={'/manager'}>
          {sectionComp}
        </Route>
      </Switch>
    </div>
  );
}

export default Main;
