import React, {useState, useEffect} from 'react';
import {
  Link,
} from 'react-router-dom';
import io from 'socket.io-client';
import host from '../constants/host'
const socket = io.connect(host,{transports: ['websocket']});


const Alert = ({username, manager}) => {
  let forPublic, forManager, forPrivate;

  useEffect(() => {
    socket.emit('giveMeAlert', {username, manager})
  })

  useEffect(() => {
    socket.on('alert', function (n) {
      forPublic = n['all'].map((item) => (<div>salam</div>))
      forManager = n['manager'].map((item) => (<div>item</div>))
      forPrivate = n[username].map((item) => (<div>my</div>))
    })
  })

  return (
    <div dir="rtl">
      {forPublic}
      {forManager}
      {forPrivate}
    </div>
  );
}

export default Alert;
