import React, {useState, useEffect} from 'react';
import {
  BrowserRouter ,
  Switch,
  Route,
  Redirect,
  useHistory
} from 'react-router-dom';
import Header from './components/header';
import Login from './components/login';
import Register from './components/register';
import Footer from './components/main-footer';
import NotFound from './components/404';
import UnderStr from './components/underStrc';
import Manager from './components/manager';
import ConnectionError from './components/connectionError'
import io from 'socket.io-client';
import host from './constants/host'
const socket = io.connect(host,{transports: ['websocket']});

const App = () => {
  const[registered, setRegistered] = useState(false);
  const[manager, setManager] = useState(false);
  const[logedInUser, setLogedInUser] = useState('');
  const history = useHistory();

  const isVerified = (e) => {
    if (e) {
      setRegistered(true)
    }
  }

  const isManager = (e) => {
    if (e) {
      setManager(true)
      localStorage.setItem('i', true)
    }
  }

  const logedInUserF = (e) => {
    setLogedInUser(e)
    localStorage.setItem('username', e)
  }

  useEffect(() => {
    if (localStorage.getItem('i')) {
      setManager(true)
    }
  }, [registered, manager])

  setTimeout(() => {
    if (!socket.connected) {
      //history.push('/error')
    }
  }, 1000);

  return (
    <BrowserRouter>
      <Switch>
        <Route path={'/'} exact>
          <Header />
          <Login isVerified={isVerified} isManager={isManager} logedInUserF={logedInUserF}/>
          <Footer />
          {(registered === true) ? <Redirect to={'/من'} /> : <Redirect to={'/'} />}
          {(manager === true) ? <Redirect to={'/manager'} /> : <Redirect to={'/'} />}
        </Route>
        <Route path={'/ورود'}>
          <Header />
          <Login isVerified={isVerified} isManager={isManager} logedInUserF={logedInUserF}/>
          <Footer />
          {(registered === true) ? <Redirect to={'/من'} /> : <Redirect to={'/ورود'} />}
          {(manager === true) ? <Redirect to={'/manager'} /> : <Redirect to={'/ورود'} />}
        </Route>
        <Route path={'/ثبت'}>
          <Header />
          <Register isVerified={isVerified}/>
          <Footer />
          {(registered === true) ? <Redirect to={'/من'} /> : <Redirect to={'/ثبت'} />}
        </Route>
        <Route path={'/من'}>
          {(registered === true) ? <Header /> : <Redirect to={'/ورود'} />}
        </Route>
        <Route path={'/manager'}>
            {(manager === true) ? <Manager logedInUser={logedInUser} /> : <Redirect to={'/ورود'} />}
        </Route>
        <Route path={'/راهنما'}>
            <UnderStr />
        </Route>
        <Route path={'/خصوصی'}>
            <UnderStr />
        </Route>
        <Route path={'/شرایط'}>
            <UnderStr />
        </Route>
        <Route path={'/error'}>
            <ConnectionError />
        </Route>
        <Route path={'*'}>
            <NotFound />
        </Route>
        {error}
      </Switch>
    </BrowserRouter>
  );
}

export default App;
