import React, {useState ,useEffect} from 'react';
import {
  Link,
  useHistory,
  useLocation
} from 'react-router-dom';

const Add = ({path, addUserInfo}) => {
  const[icon , setIcon] = useState('add')

  const submit = () => {
    document.forms[".addUserInfo-cont"].submit();
  }

  const history = useHistory();
  const location = useLocation();

  useEffect(() => {
    if (location.pathname == '/manager/add/user') {
      setIcon('how_to_reg')

    }
  })

  return (
    <Link to={path}
    className="mngr-add-info-cont"
    dir="rtl"
    onClick={
      addUserInfo ? submit : ''
      }
    >
      <div className="mngr-add-info" dir="rtl">
        <div className="mngr-add-info-icon"dir="rtl">
        <span class="material-icons" style={{fontSize:50, color:'white'}} >{icon}</span>
        </div>
      </div>
    </Link>
  );
}

export default Add;
