import React, {useState, useEffect} from 'react';
import {
  Link,
} from 'react-router-dom';
import UnderStr from './underStrc';
import Section from './manager-sec';
import {default as sectionContent} from '../constants/section-content.js'

const AddInfo = () => {
  const[list, setList] = useState(0)
  const[sectionComp, setSectionComp] = useState(sectionContent)

  const onTicked = (e, k) =>{
    delete sectionContent[k];
    setList(list+1)
  }

  useEffect(()=>{
    setSectionComp(
      sectionContent.map((item, i) => (
        <Section
        id={i}
        key={item[0]}
        path={item[3]}
        title={item[1]}
        content={item[2]}
        onTicked={onTicked}
        />
      ))
    )
  }, [list])

  return (
    <div className="addInfo-cont">
    </div>
  );
}
export default AddInfo;
