import React, {useState, useEffect} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Link,
  Route,
  Redirect,
  useLocation,
} from 'react-router-dom';
import Header from './components/header';
import Login from './components/login';
import Register from './components/register';
import Footer from './components/main-footer';
import NotFound from './components/404';

const App = () => {
  const[registered, setRegistered] = useState(false);

  const isVerified = (e) => {
    if (e) {
      setRegistered(true)
      localStorage.setItem('isVerified', true)
    }
  }

  useEffect(() => {
    if (localStorage.getItem('isVerified')) {
      setRegistered(true)
    }
    console.log(useLocation().pathname);
  }, [registered])

  return (
    <Router>
      <Switch>
        <Route path={'/'} exact>
          <Header />
          <Login />
          <Footer />
        </Route>
        <Route path={'/login'}>
          <Header />
          <Login/>
          <Footer />
          {(registered === true) ? <Redirect to={'/home'} /> : <Redirect to={'/login'} />}
        </Route>
        <Route path={'/register'}>
          <Header />
          <Register isVerified={isVerified}/>
          <Footer />
          {(registered === true) ? <Redirect to={'/home'} /> : <Redirect to={'/register'} />}
        </Route>
        <Route path={'/home'}>
          {(registered === true) ? (<Header />) : <Redirect to={'/login'} />}
        </Route>
        <Route path={'*'}>
            <NotFound />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
