import React, {useState, useEffect} from 'react';
import {
  Router,
  Route,
  Link,
} from 'react-router-dom';
import {PathLine} from 'react-svg-pathline'
import UnderStr from './underStrc';
import Section from './manager-sec';
import io from 'socket.io-client';
import host from '../constants/host'
import moment from 'jalali-moment';
const socket = io.connect(host,{transports: ['websocket']});

let day = [];
let thisDay = parseInt(moment().locale('fa').format('D'));
let thisM = parseInt(moment().locale('fa').format('M'));
let grph = [];

//console.log(thisDay);

for (var i = 0; i < 30; i++) {
  grph.push({x:thisDay, y:100});
  thisDay --;
}

for (var i = 1; i <= 30; i++) {
  day.push({x:i, y:100})
}

//console.log(grph);


const Graph = ({activity}) => {
  //console.log(activity);
  const [data, setData] = useState()
  const [g, setG] = useState(grph)
  let c = 0;
  let h = {};
  let pLine = [];

  //console.log(activity);

  useEffect(() => {
    const sd = activity.map((item, i) =>{
      if (item != '') {
        let d = item.split(' ')[0].split('/')[2];
        //console.log(item.split(' ')[0]);
        let f = activity[i+1];
        h[d] = c+1;
        if (f) {
          //console.log(item, activity[i+1]);
          if (d != f.split(' ')[0].split('/')[2]) {
            c = 0;
          }else {
            c++
          }
        }
        return h
      }
    });
    console.log("###########################################");
    setData(sd[0]);
  }, [1])



  useEffect(() => {
    //console.log(data);
    day.forEach((item, i) => {
      if (data) {
        //console.log(data);
        let xX = 10*(item.x), yY = 10*(item.y);
        for (var j = 0; j < Object.keys(data).length; j++) {
          if (xX == 10*(Object.keys(data)[j])) {
            //console.log(100-Object.values(data)[j]);
            pLine.push({x:xX, y:(1000-Object.values(data)[j])})
          }else {
            if (!pLine[i]) {
              pLine.push({x:xX, y:yY})
            }
          }
        }
      }else {
        setG(grph);
        console.log('DDDDDDDDDDDDDD');
      }
    });
    if (pLine != '') {
      setG(pLine);
      //console.log(pLine);
    }
    console.log(g);
  }, [data])

  return (
    <>
      <svg className="activityDiag" width={210} height={70}>
        <defs>
          <linearGradient id="gradient-303647730" x1="0" x2="0" y1="1" y2="0">
            <stop offset="10%" stopColor="#BBDEFB"></stop>
            <stop offset="100%" stopColor="#00A1F1"></stop>
        </linearGradient>
        </defs>
        <PathLine
         stroke="url(#gradient-303647730)"
         className="activityLine"
         points={g}
         strokeWidth="3"
         fill="none"
         strokeLinecap="round"
         r={0}
         />
      </svg>
      <h1></h1>
      </>
  );
}
export default Graph;
