import React, {useState, useEffect} from 'react';
import {
  Router,
  Route,
  Link,
} from 'react-router-dom';
import VerifiedUserIcon from '@material-ui/icons/VerifiedUser';
import {PathLine} from 'react-svg-pathline'
import UnderStr from './underStrc';
import Section from './manager-sec';
import io from 'socket.io-client';
import host from '../constants/host'
import Graph from './graph.js'

const UserComp = ({name, username, verified, email, activity, manager}) => {
  const[v, setV] = useState('')
  useEffect(() => {
    if (verified) {
      setV(<span className="Verified">تایید شده</span>)
    }else {
      setV(<span className="noVerified">در انتظار تایید</span>)
    }
    if (manager) {
      setV(<VerifiedUserIcon />)
    }else {
      setV('')
    }
  },[1])

  return (
      <div className='userCont' dir="rtl">
        <h1>{name+" "+manager}{v}</h1>
        <p dir="ltr">@{username}</p>
        <Graph activity={activity} />
      </div>
  );
}
export default UserComp;
