import React, {useState, useEffect} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from 'react-router-dom';
import UnderStr from './underStrc';
import Section from './manager-sec';
import sectionContent from '../constants/section-content.js'

let locStr = []

const Main = () => {
  const[list, setList] = useState(0)
  const[sectionComp, setSectionComp] = useState(sectionContent)

  const onTicked = (e, k) =>{
    delete sectionContent[k];
    setList(list+1)
    locStr.push(k);
    console.log(locStr);
    localStorage.setItem('sec', JSON.string(locStr))
  }

  useEffect(()=>{
    if ( localStorage.getItem('sec')) {
      let l = localStorage.getItem('sec');
      for (var i = 0; i < l.length; i++) {
      delete sectionContent[l[i]];
      console.log(sectionContent);
      }
    }
    console.log(localStorage.getItem('sec'));
  }, [list])

  useEffect(()=>{
    setSectionComp(
      sectionContent.map((item, i) => (
        <Section
        id={i}
        key={item[0]}
        path={item[3]}
        title={item[1]}
        content={item[2]}
        onTicked={onTicked}
        />
      ))
    )
  }, [list])

  return (
    <div className="section-cont">
      <Switch>
        <Route path={'/manager/add'}>
          <UnderStr />
        </Route>
        <Route path={'/manager'}>
          {sectionComp}
        </Route>
      </Switch>
    </div>
  );
}

export default Main;
