import React, {useState} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory,
} from 'react-router-dom';

const Section = ({path, title, content}) => {
  const[counter, setCounter] = useState(0)
  const[status, setStatus] = useState(false)
  const[decoration, setDecoration] = useState('none')
  const[opacity, setOpacity] = useState(1)
  const[ticked, setTicked] = useState('white')

  return (
    <div className="section" dir="rtl" done={status} onClick={
      () => {
        useHistory().push('./manager/add')
      }
    }>
      <h1 className="section-title" style={{textDecoration:decoration, opacity:opacity}}>
        <span>{title}</span>
        <div className="done" style={{backgroundColor:ticked}} onClick={
          (e) => {
            e.stopPropagation();
            if (counter == 0) {
              setStatus(true);
              setDecoration('line-through')
              setCounter(1);
              setOpacity(.5);
              setTicked('#00A1F1')
            }else {
              setStatus(false);
              setDecoration('none')
              setCounter(0);
              setOpacity(1);
              setTicked('white')
            }
          }
        }></div>
      </h1>
      <p className="section-contant" style={{textDecoration:decoration, opacity:opacity}}>{content}</p>
    </div>
  );
}

export default Section;
