import React, {useState, useEffect} from 'react';

let c = [];
let color = 'white';
let rand = Math.round(Math.random()*30);
for (var i = 1; i <= 100; i++) {
  c.push(i)
}

const Random = () => {

  const cell = c.map((item, i) => (<p key={i} id={i} style={{backgroundColor: color}} className="randomCell"></p>))

  useEffect(()=>{
    for (var i = 0; i < 13; i++) {
      if (document.getElementById(''+Math.round(Math.random()*30)+1+'').style.backgroundColor != '#87255B') {
        document.getElementById(''+Math.round(Math.random()*30)+'').style.backgroundColor = '#87255B';
      }

    }
  })

  return (
    <p className="random">{cell}</p>
  );
}
export default Random;
