import React, {useState ,useEffect} from 'react';
import {
  Link,
  useHistory,
  useLocation
} from 'react-router-dom';
import io from 'socket.io-client';
import host from '../constants/host'
const socket = io.connect(host,{transports: ['websocket']});

const Add = ({path, addUserInfo, sentToServer}) => {
  const[icon , setIcon] = useState('add')
  const history = useHistory();
  const location = useLocation();

  const submit = (e) => {
    if (location.pathname == '/manager/add/user') {
      socket.emit('addUserInfo', addUserInfo)
      sentToServer(true)
    }
  }

  useEffect(() => {
    if (location.pathname == '/manager/add/user') {
      setIcon('how_to_reg')
    }else {
      setIcon('add')
    }
  })

  return (
    <Link to={path}
    className="mngr-add-info-cont"
    dir="rtl"
    onClick={()=>{
      (addUserInfo != '') ? submit : sentToServer(true)
      }
    }
    >
      <div className="mngr-add-info" dir="rtl">
        <div className="mngr-add-info-icon"dir="rtl">
        <span class="material-icons" style={{fontSize:50, color:'white'}} >{icon}</span>
        </div>
      </div>
    </Link>
  );
}

export default Add;
