import React, {useState, useEffect} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Link,
  Route,
  Redirect,
} from 'react-router-dom';
import Header from './components/header';
import Login from './components/login';
import Register from './components/register';
import Footer from './components/main-footer';

const App = () => {
  const[registered, setRegistered] = useState(false);

  const isVerified = (e) => {
    if (e) {
      setRegistered(true)
      localStorage.setItem('isVerified', true)
    }
  }

  /*useEffect(() => {
    if (localStorage.getItem('isVerified')) {
      setRegistered(true)
    }
  }, [registered])*/

  return (
    <Router>
      <Switch>
        <Route path={'/'} exact>
          <Header />
          <Login />
          <Footer />
        </Route>
        <Route path={'/login'}>
          <Header />
          <Login/>
          <Footer />
          {(registered === true) ? <Redirect to={'/home'} /> : <Redirect to={'/login'} />}
        </Route>
        <Route path={'/register'}>
          <Header />
          <Register isVerified={isVerified}/>
          <Footer />
          {(registered === true) ? <Redirect to={'/home'} /> : <Redirect to={'/register'} />}
        </Route>
        <Route path={'/home'}>
          {(registered === true) ? (<Header />) : <Redirect to={'/login'} />}
        </Route>
        <Route path={'*'}>
            404 Error
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
